import React from "react";
import { Button, ListGroup, Row, Col, Container, Tab, Nav, } from "react-bootstrap";
import CardContainer from "../components/CardContainer";
import logo from "../assets/ithnain.png";
import { login } from "../API/apiAuth";
import { DataContext } from "../stateManagement/context";
import { parseArray } from "../helpers/Converters";

class ShowPatientsView extends React.Component {
  static contextType = DataContext;

  state = {
    username: "",
    password: ""
  };

  componentWillMount() {
    if (this.context.educatorId == "") {
      this.props.history.goBack();
      // console.log(this.props)
    }
  }

  render() {
    console.log(this.context.chats);
    let { chats } = this.context;
    if (!this.context.chats[0].id) {
      chats = parseArray(this.context.chats);
    }
    const patients = chats.map(chat => {
      return (
        <Nav.Item>
        <Nav.Link key={chat.id} eventKey={chat.id}>{chat.patientName}</Nav.Link>
      </Nav.Item>
      );
    });
    const patientsInfo = chats.map(chat => {
        return (
        <Tab.Pane key={chat.id} eventKey={chat.id}>
                <h1> {chat.patientName}{" "}{chat.patientId}</h1>
            </Tab.Pane>
        )
    })
    return (
      <Container
        fluid
        style={{
          backgroundColor: "#0a122a",
          justifyContent: "space-around",
          alignItems: "center",
          display: "flex"
        }}
      >
              {/* <Row>
                  <Col sm={4} lg={3}>

                  <ListGroup as="ul">
                  <ListGroup.Item as="li" active>
                  Cras ju saad fas dfasd faad fasto odio
                  </ListGroup.Item>
                  {patients}
                  </ListGroup>

                  </Col>
                  </Row>
                */}



        <Tab.Container id="left-tabs-example" defaultActiveKey="first">
          <Row>
            <Col sm={4} >
        <CardContainer width="100%">
              <Nav variant="pills" className="flex-column">
                <Nav.Item>
                  <Nav.Link eventKey="first">Tab 1</Nav.Link>
                </Nav.Item>
                {patients}
              </Nav>
            </CardContainer>
            </Col>

            <Col sm={8}>
            <CardContainer width="100%">
              <Tab.Content>
                <Tab.Pane eventKey="first">
                  <h1>Doneeeee</h1>
                </Tab.Pane>
                {patientsInfo}
              </Tab.Content>
              </CardContainer>
            </Col>
             </Row>
        </Tab.Container>
      </Container>
    );
  }
}

export default ShowPatientsView;
