import React from "react";
import { Button, ListGroup, Row, Col, Container } from "react-bootstrap";
import CardContainer from "../components/CardContainer";
import logo from "../assets/ithnain.png";
import { login } from "../API/apiAuth";
import { DataContext } from "../stateManagement/context";
import {parseObjectOfArrays} from '../helpers/Converters'

class ShowPatientsView extends React.Component {
  static contextType = DataContext;

  state = {
    username: "",
    password: ""
  };

  componentDidMount() {
    if (this.context.educatorId == "") {
      this.props.history.goBack();
      // console.log(this.props)
    }
  }

  render() {
    console.log(this.context.chats);
    let {chats} = this.context;
    if(!this.context.chats[0]){
       chats =  parseObjectOfArrays(this.context.chats)
    }
    const patients = chats.map(chat => {
        return(
            <ListGroup.Item key={chat.id} as="li" >
                {chat.patientName}
              </ListGroup.Item>
        )
    })
    return (
      <Container
        fluid
        style={{
          display: "flex",
          justifyContent: "space-around",
          alignItems: "center",
          height: "100vh",
          backgroundColor: "#0a122a"
        }}
      >
          <CardContainer width="80%">
              <Row>
                  <Col sm={4} lg={3}>

            <ListGroup as="ul">
              <ListGroup.Item as="li" active>
                Cras ju saad fas dfasd faad fasto odio
              </ListGroup.Item>
             {patients}
            </ListGroup>

                  </Col>
              </Row>



            </CardContainer>


      </Container>
    );
  }
}

export default ShowPatientsView;
